fetch("http://localhost:8085/eOrderButler/getShoppingOrderById/1", {mode: 'no-cors'})
    .then(function(response) {
        console.log(response);
    }).catch(function(error) {
    alert("HTTP-Error");
});




class Dataview extends Component {
    constructor(props) {
        super(props);
        this.state = {
            hits: [],
        };
    }
    //
    // componentDidMount() {
    //     fetch(API)
    //         .then(response => response.json())
    //         .then(data => this.setState({ hits: data.hits }));
    // }


    render() {
        return(
            <table className="table">
                <thead>
                <tr>
                    <th>Date</th>
                    <th>Merchant</th>
                    <th>Order Number</th>
                    <th>Total</th>
                    <th>Status</th>
                </tr>
                </thead>

                <tbody>
                    {PostData.map((postDetail, index) => {
                    return (
                        <tr key={index}>
                            <td>{new Date(postDetail.date).toISOString().slice(0,10)}</td>
                            <td>{postDetail.merchant}</td>
                            <td>{postDetail.orderNumber}</td>
                            <td>{postDetail.totalPrice}</td>
                            <td>{postDetail.status}</td>
                        </tr>
                    )
                })}
                </tbody>
            </table>
        )
    }
}


export default Dataview;
