//     });

// fetch(API)
//     .then(response => response.json())
//     .then((resp) => resp.json())
//     .then(function(data) {
//         console.log(data);
//     }).catch(function(error) {
//     alert("HTTP-Error");
// });

class Main extends React.Component {
    constructor (props) {
        super (props);
        this.state = {
            PostData: [],
        };
        this.handleAddTodo = this.handleAddTodo.bind(this);
    }

    handleAddTodo (text) {
        //this.setState(prevState => ({ todos : [...prevState. todos , text] })) ;
    }


    async componentDidMount() {
        // fetch(API)
        //     .then(response => response.json())
        //     .then(data => this.setState({ todos: data.todos }))
        //     .catch(function(error) {
        //         console.log(error)
        //         alert(error.message);
        //     });


        await this.setState({PostData: a});

        // var cors = require('cors');
        // app.use(cors()); // Use this after the variable declaration

        // var myInit = {method: 'GET',
        //                 headers : {
        //                     'Content-Type': 'application/json',
        //                     'Accept': 'application/json'
        //                 },
        //                 mode: 'no-cors',
        //                 cache: 'default'
        // };
        //
        // let myRequest = new Request(url, myInit);
        //
        // //await fetch(myRequest)
        // await fetch(url)
        //     .then(response => response)
        //      //.then(response => response.json())
        //      //.then(data => this.setState({PostData: data}))
        //     .then(data => {console.log(data);}).
        //     catch(err => {alert("Error Reading data " + err);
        // });

        let headers = new Headers();

        headers.append('Content-Type', 'application/json');
        headers.append('Accept', 'application/json');
        headers.append('Origin','http://localhost:3001');

        fetch(url, {
            mode: 'cors',
            credentials: 'include',
            method: 'POST',
            headers: headers
        })
            .then(response => response.json())
            .then(json => console.log(json))
            .catch(err => {alert("Error Reading data " + err);
            });

        console.log(this.state)
        //debugger
    }


    render () {
        //debugger;
        return (
            <div className ="main" >
                <SearchBar />
                <div className ="dateview" >
                    <Dataview PostData = { this.state.PostData }/>
                </div>
                <div className ="add" >
                    <UserInput handleAddTodo= { this.handleAddTodo } />
                </div>
            </div>
        ) ;
    }
}


export default Main;
