import React from 'react';
import Dataview from './Dataview';
import UserInput from './UserInput';
import SearchBar from './SearchBar' ;
import UserService from "../services/user.service";

import axios, * as others from 'axios';
import authHeader from '../services/auth-header';


const url = "http://localhost:8085/eOrderButler/getAllShoppingOrders";

// const localDir = "../assets/json_file/userOrder.json";



class Main extends React.Component {
    constructor (props) {
        super (props);
        this.state = {
            PostData: [],
        };
        this.handleAddTodo = this.handleAddTodo.bind(this);
    }

    handleAddTodo (text) {
        console.log(text)
        //this.setState(prevState => ({ todos : [...prevState. todos , text] })) ;
    }

    async componentDidMount() {

        await axios.get(url, { headers: authHeader() })
            .then((response) => {
                console.log(response);
                this.setState({PostData: response.data})
            })
            .catch((error)=>{
                console.log(error);
            });
    }


    render () {
        //debugger;
        return (
            <div className ="main" >
                <SearchBar />
                <div className ="dataview" >
                    <Dataview PostData = { this.state.PostData }/>
                </div>
                <div className ="add" >
                    <UserInput handleAddTodo= { this.handleAddTodo } />
                </div>
            </div>
        ) ;
    }
}


export default Main;
