    .then(function(response) {
        console.log(response);
    }).catch(function(error) {
    console.log('Request failed', error);
    alert("HTTP-Error");
});




class Dataview extends Component {
    constructor(props) {
        super(props);

        this.state = {
            hits: [],
        };
    }

    componentDidMount() {
        fetch(API)
            .then(response => response.json())
            .then(data => this.setState({ hits: data.hits }));
    }


    render() {
