import React , {Component} from 'react' ;
import icon from "../assets/images/logo.svg" ;
import logo from "../assets/images/logo.svg" ;
import ReactModalLogin from "react-modal-login";
import Modal from "react-responsive-modal";
import { facebookConfig, googleConfig } from "./social-config";
import LoginModal from "react-login-modal-sm";

class TopNavBar extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            showModal: false,
            loading: false,
            error: null,
            sign: false,
            login: false,
        };
    }

    toggleModal = () => {
        this.setState({ showModal: !this.state.showModal });
    };

    handleLoginWithFacebook = () => {
        // Do something when 'Login with Facebook' is clicked
        console.log("Login with Facebook...");
    };

    handleSignupByEmail = (email, username, password) => {
        // Do something when 'Signup by email' is clicked
        console.log("Sign up by email...");
    };

    onOpenModal = () => {
        this.setState({ sign: true });
    };

    onOpenModalLogin = () => {
        this.setState({ login: true });
    };

    onCloseModal = () => {
        this.setState({ sign: false });
    };

    onCloseModalclose = () => {
        this.setState({ login: false });
    };

    openModal() {
        this.setState({
            showModal: true
        });
    }

    closeModal() {
        this.setState({
            showModal: false,
            error: null
        });
    }

    onLoginSuccess(method, response) {
        console.log("logged successfully with " + method);
    }

    onLoginFail(method, response) {
        console.log("logging failed with " + method);
        this.setState({
            error: response
        });
    }

    startLoading() {
        this.setState({
            loading: true
        });
    }

    finishLoading() {
        this.setState({
            loading: false
        });
    }

    afterTabsChange() {
        this.setState({
            error: null
        });
    }



    render() {
        const { login, sign } = this.state;
        const customUsernameRegex = /^[a-zA-Z0-9_]{5,}/;

        return (
        <div className="container">
            <nav className="selfnav navbar navbar-expand-md bg-light sticky-top">
                <img src={logo} className="App-logo" alt="logo" />
                <a className="navbar-brand text-dark" href="#">EOrderButler</a>
                <button className="navbar-toggler navbar-toggler-right" type="button" data-toggle="collapse"
                        data-target="#navb" aria-expanded="true">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div id="navb" className="navbar-collapse collapse">
                    <ul className="navbar-nav">
                        <li className="nav-item active text-dark">
                            <a className="nav-link" href="#">Home</a>
                        </li>
                        <li className="nav-item text-dark">
                            <a className="nav-link" href="#">Details</a>
                        </li>
                    </ul>

                    <ul className="nav navbar-nav ml-auto">
                        <li className="nav-item">
                            <a className="nav-link text-dark" href="#"><span className="fas fa-user"></span> Sign Up</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link text-dark" href="#"><span className="fas fa-sign-in-alt"></span> Login</a>
                        </li>
                    </ul>
                </div>

                <div className="TEST">
                    <LoginModal
                        showModal={this.state.showModal}
                        toggleModal={this.toggleModal}
                        onLoginFacebook={this.handleLoginWithFacebook}
                        onSignupEmail={this.handleSignupByEmail}
                        usernameRegex={customUsernameRegex}
                    />

                    <button
                        className="test-btn btn btn-primary btn-lg"
                        onClick={this.toggleModal}
                    >
                        Login
                    </button>
                </div>
            </nav>
        </div>
        );
    }
}




export default TopNavBar ;
