import React from 'react';
import Dataview from './Dataview';
import UserInput from './UserInput';
import SearchBar from './SearchBar' ;

// profile and data view's parent

const API = "http://localhost:8085/eOrderButler/getShoppingOrderById/1";

const proxyurl = "https://cors-anywhere.herokuapp.com/";
fetch(proxyurl + API) // https://cors-anywhere.herokuapp.com/https://example.com
    .then(response => response.text())
    .then(contents => console.log(contents))
    .catch(() => console.log("Can’t access " + API + " response. Blocked by browser?"))

// fetch(API)
//     .then(response => response.json())
//     .then((resp) => resp.json())
//     .then(function(data) {
//         console.log(data);
//     }).catch(function(error) {
//     alert("HTTP-Error");
// });


class Main extends React.Component {
    constructor (props) {
        super (props);
        this.state = {
            todos: [],
        };
        this.handleAddTodo = this.handleAddTodo.bind(this);
    }

    handleAddTodo (text) {
        this.setState(prevState => ({ todos : [...prevState. todos , text] })) ;
    }


    componentDidMount() {
        // fetch(API)
        //     .then(response => response.json())
        //     .then(data => this.setState({ todos: data.todos }))
        //     .catch(function(error) {
        //         console.log(error)
        //         alert(error.message);
        //     });

        // fetch("http://localhost:8085/eOrderButler/getShoppingOrderById/1", {mode: 'no-cors'})
        //     .then(function(response) {
        //         console.log(response);
        //     }).catch(function(error) {
        //     alert("HTTP-Error");
        // });
    }


    render () {
        return (
            <div className ="main" >
                <SearchBar />
                <div className ="dateview" >
                    <Dataview todos= { this.state.todos }/>
                </div>
                <div className ="add" >
                    <UserInput handleAddTodo= { this.handleAddTodo } />
                </div>
            </div>
        ) ;
    }
}


export default Main;
