fetch("http://localhost:8085/eOrderButler/getShoppingOrderById/1", {mode: 'no-cors'})
    .then(function(response) {
        console.log(response);
    }).catch(function(error) {
    alert("HTTP-Error");
});


class Main extends React.Component {
    constructor (props) {
        super (props);
        this.state = {
            todos: [],
            hits: [],
        };
        this.handleAddTodo = this.handleAddTodo.bind(this);
    }

    handleAddTodo (text) {
        this.setState(prevState => ({ todos : [...prevState. todos , text] })) ;
    }

    handleDeleteTodo (index) {
        this .setState((prevState) => {
            return {
                todos : [...prevState. todos . slice ( 0 , index) ,
                    ...prevState. todos . slice (index + 1 , prevState. todos . length )] ,
            } ;
        }) ;
    }

    componentDidMount() {
        fetch(API,{
            method: "GET",
            headers: {
                "access-control-allow-origin" : "*",
                "Content-type": "application/json; charset=UTF-8"
            }})
            .then(results => results.json())
            .then(info => {
                const results = info.data.map(x => {
                    return {
                        id: x.id,
                        slug: x.slug,
                        name: x.name,
                        address_1: x.address_1,
                        address_2: x.address_2,
                        city: x.city,
                        state: x.state,
                        postal_code: x.postal_code,
                        country_code: x.country_code,
                        phone_number: x.phone_number,
                    }
                })
                this.setState({warehouses: results, lastPage: info.last_page});
            })
            .then(console.log(this.state.warehouses))
    }

    render () {
        return (
            <div className ="main" >
                <SearchBar />
                <div className ="dateview" >
                    <Dataview todos= { this.state.todos }
                              handleDeleteTodo= { this.handleDeleteTodo } />
                </div>
                <div className ="add" >
                    <UserInput handleAddTodo= { this.handleAddTodo } />
                </div>
            </div>
        ) ;
    }
}


export default Main;
