fetch("http://localhost:8085/eOrderButler/getShoppingOrderById/1", {mode: 'no-cors'})
    .then(function(response) {
        console.log(response);
    }).catch(function(error) {
    alert("HTTP-Error");
});


class Main extends React.Component {
    constructor (props) {
        super (props);
        this.state = {
            todos: [],
            hits: [],
        };
        this.handleAddTodo = this.handleAddTodo.bind(this);
    }

    handleAddTodo (text) {
        this.setState(prevState => ({ todos : [...prevState. todos , text] })) ;
    }

    handleDeleteTodo (index) {
        this .setState((prevState) => {
            return {
                todos : [...prevState. todos . slice ( 0 , index) ,
                    ...prevState. todos . slice (index + 1 , prevState. todos . length )] ,
            } ;
        }) ;
    }

    componentDidMount() {
        fetch(API)
            .then(response => response.json())
            .then(data => this.setState({ hits: data.hits }));
    }

    render () {
        return (
            <div className ="main" >
                <SearchBar />
                <div className ="dateview" >
                    <Dataview todos= { this.state.todos }
                              handleDeleteTodo= { this.handleDeleteTodo } />
                </div>
                <div className ="add" >
                    <UserInput handleAddTodo= { this.handleAddTodo } />
                </div>
            </div>
        ) ;
    }
}


export default Main;
