import React , {Component} from 'react' ;
import logo from "../assets/images/logo.svg" ;
import ReactModalLogin from "react-modal-login";
import Modal from "react-responsive-modal";
import { facebookConfig, googleConfig } from "./social-config";

class TopNavBar extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            showModal: false,
            loading: false,
            error: null
        };
    }

    onOpenModal = () => {
        this.setState({ sign: true });
    };

    onOpenModalLogin = () => {
        this.setState({ login: true });
    };

    onCloseModal = () => {
        this.setState({ sign: false });
    };

    onCloseModalclose = () => {
        this.setState({ login: false });
    };

    openModal() {
        this.setState({
            showModal: true
        });
    }

    closeModal() {
        this.setState({
            showModal: false,
            error: null
        });
    }

    onLoginSuccess(method, response) {
        console.log("logged successfully with " + method);
    }

    onLoginFail(method, response) {
        console.log("logging failed with " + method);
        this.setState({
            error: response
        });
    }

    startLoading() {
        this.setState({
            loading: true
        });
    }

    finishLoading() {
        this.setState({
            loading: false
        });
    }

    afterTabsChange() {
        this.setState({
            error: null
        });
    }



    render() {
        return (
        <div className="container">
            <nav className="selfnav navbar navbar-expand-md bg-light sticky-top">
                <img src={logo} className="App-logo" alt="logo" />
                <a className="navbar-brand text-dark" href="#">EOrderButler</a>
                <button className="navbar-toggler navbar-toggler-right" type="button" data-toggle="collapse"
                        data-target="#navb" aria-expanded="true">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div id="navb" className="navbar-collapse collapse">
                    <ul className="navbar-nav">
                        <li className="nav-item active text-dark">
                            <a className="nav-link" href="#">Home</a>
                        </li>
                        <li className="nav-item text-dark">
                            <a className="nav-link" href="#">Details</a>
                        </li>
                    </ul>

                    <ul className="nav navbar-nav ml-auto">
                        <li className="nav-item">
                            <a className="nav-link text-dark" href="#"><span className="fas fa-user"></span> Sign Up</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link text-dark" href="#"><span className="fas fa-sign-in-alt"></span> Login</a>
                            <button className="btn btn-primary-outline" id="login" onClick={this.onOpenModalLogin}>Login</button>
                        </li>
                    </ul>
                </div>

                <div>
                    <button onClick={() => this.openModal()}>Open Modal</button>

                    <ReactModalLogin
                        visible={this.state.showModal}
                        onCloseModal={this.closeModal.bind(this)}
                        loading={this.state.loading}
                        error={this.state.error}
                        tabs={{
                            afterChange: this.afterTabsChange.bind(this)
                        }}
                        loginError={{
                            label: "Couldn't sign in, please try again."
                        }}
                        registerError={{
                            label: "Couldn't sign up, please try again."
                        }}
                        startLoading={this.startLoading.bind(this)}
                        finishLoading={this.finishLoading.bind(this)}
                        providers={{
                            facebook: {
                                config: facebookConfig,
                                onLoginSuccess: this.onLoginSuccess.bind(this),
                                onLoginFail: this.onLoginFail.bind(this),
                                label: "Continue with Facebook"
                            },
                            google: {
                                config: googleConfig,
                                onLoginSuccess: this.onLoginSuccess.bind(this),
                                onLoginFail: this.onLoginFail.bind(this),
                                label: "Continue with Google"
                            }
                        }}
                    />
                </div>

                <Modal open={login} onClose={this.onCloseModalclose}>

                    <div className="modal-body">
                        <h2>Login and Get <span>Started</span></h2>
                        <span className="subtitle">Just fill in the form below</span>
                        <form className="contact-form form-validate4" novalidate="novalidate">
                            <div className="form-group">
                                <input className="form-control" type="email" name="email" placeholder="E-mail" required="" autocomplete="off" aria-required="true" />
                            </div>
                            <div className="form-group">
                                <input type="password" name="pass" className="form-control" placeholder="Password" required="" autocomplete="off" aria-required="true" />
                            </div>
                            <input className="btn btn-md btn-primary btn-center" id="login_btn" type="button" value="Login" />
                        </form>
                    </div>
                </Modal>
                
            </nav>
        </div>

        );
    }
}

export default TopNavBar ;
