import React from 'react';
import Dataview from './Dataview';
import UserInput from './UserInput';
import SearchBar from './SearchBar' ;
import UserService from "../services/user.service";
import ReactTable from 'react-table';

import axios, * as others from 'axios';
import authHeader from '../services/auth-header';


const url_getall = "http://localhost:8085/eOrderButler/getAllShoppingOrders";


class Detail extends React.Component {
    constructor (props) {
        super (props);
        this.state = {
            PostData: [],
        };

    }


    async componentDidMount() {
        await axios.get(url_getall, { headers: authHeader() })
            .then((response) => {
                console.log(response);
                this.setState({PostData: response.data})
            })
            .catch((error)=>{
                console.log(error);
            });
    }


    render () {
        //debugger;
        // return (
        //     <div className ="main" >
        //         <div className ="dataview" >
        //             <Dataview PostData = { this.state.PostData }/>
        //         </div>
        //     </div>
        // ) ;
        const columns = [
            {
                Header: () => (
                    <div />
                ),
                accessor: 'name',
                maxWidth: 300,
                Cell: row => (
                    <div className='first-column'>{row.value}</div>
                )
            }
        ];

        const subComponent = row => {
            return (
                <div>
                    {row.original.types.map((type, id) => {
                        return (
                            <div className='subRow' key={ id }>{ type.name }</div>
                        );
                    })}
                </div>
            );
        };

        const data = [
            {
                id: '12345',
                name: 'sports',
                types: [
                    {
                        name: 'basketball',
                        id: '1'
                    },
                    {
                        name: 'soccer',
                        id: '2'

                    },
                    {
                        name: 'baseball',
                        id: '3'
                    }
                ]
            },
            {
                id: '678910',
                name: 'food',
                types: [
                    {
                        name: 'pizza',
                        id: '4'
                    },
                    {
                        name: 'hamburger',
                        id: '5'

                    },
                    {
                        name: 'salad',
                        id: '6'
                    }
                ]
            }
        ];

        return (
            <ReactTable data={ data }
                        columns={ columns }
                        SubComponent={ subComponent }
                        expandedRows={ true }
                        resizable={ false }
                        minRows={ 1 }
                        pageSize={ 8 }
                        showPageSizeOptions={ false } />
        );
    }
}


export default Detail;
